import React, { Component } from 'react'
import './App_chat.css'
import MessageList from "./MessageList";

class App_chat extends Component {
    constructor(props) {
        super(props)
        this.state = {
            messages: [
                { body: "Connecting..." },
                { author: "You", body: "Hello!", me: true },
                { author: "Them", body: "Hey there!"  },
            ],
        }
    }
    render() {
        return (
            <div className="App">
                <MessageList messages={this.state.messages} />
            </div>
        )
    }
}

export default App_chat
