import React, { Component } from 'react'
import './App_chat.css'
import MessageList from "./MessageList";
import MessageForm from "./MessageForm";
import $ from 'jquery'
import {SyncClient} from "twilio-sync";
const Chat = require('twilio-chat');

class App_chat extends Component {
    constructor(props) {
        super(props)
        this.state = {
            messages: [],
            username: null,
            channel: null,
        }
    }
    componentDidMount = () => {
        this.getToken()
            .then(this.createChatClient)
            .then(this.joinGeneralChannel)
            .then(this.configureChannelEvents)
            .catch((error) => {
                this.addMessage({ body: `Error: ${error.message}` })
            })
    }
    getToken = () => {
        return new Promise((resolve, reject) => {
            this.setState({
                messages: [...this.state.messages, { body: `Connecting...` }],
            })

            $.getJSON('/token', (token) => {
                this.setState({ username: token.identity })
                resolve(token)
            }).fail(() => {
                reject(Error("Failed to connect."))
            })
        })
    }
    addMessage = (message) => {
        const messageData = { ...message, me: message.author === this.state.username }
        this.setState({
            messages: [...this.state.messages, messageData],
        })
    }


    createChatClient = async() => {
       /* return new Promise((resolve, reject) => {
            resolve(new TwilioChat(token.jwt))
        })*/
        var _syncClient = new SyncClient(getNewToken());

        _syncClient.on('tokenAboutToExpire', function() {
            _syncClient.updateToken(getNewToken());
        });
    }
    joinGeneralChannel = (chatClient) => {
        return new Promise((resolve, reject) => {
            chatClient.getSubscribedChannels().then(() => {
                chatClient.getChannelByUniqueName('general').then((channel) => {
                    // ...
                }).catch(() => this.createGeneralChannel(chatClient))
            }).catch(() => reject(Error('Could not get channel list.')))
        })
    }

    createGeneralChannel = (chatClient) => {
        return new Promise((resolve, reject) => {
            this.addMessage({ body: 'Creating general channel...' })
            chatClient
                .createChannel({ uniqueName: 'general', friendlyName: 'General Chat' })
                .then(() => this.joinGeneralChannel(chatClient))
                .catch(() => reject(Error('Could not create general channel.')))
        })
    }

    configureChannelEvents = (channel) => {
        channel.on('messageAdded', ({ author, body }) => {
            this.addMessage({ author, body })
        })

        channel.on('memberJoined', (member) => {
            this.addMessage({ body: `${member.identity} has joined the channel.` })
        })

        channel.on('memberLeft', (member) => {
            this.addMessage({ body: `${member.identity} has left the channel.` })
        })
    }
    handleNewMessage = (text) => {
        if (this.state.channel) {
            this.state.channel.sendMessage(text)
        }
    }
    render() {
        return (
            <div className="App">
                <MessageList messages={this.state.messages} />
                <MessageForm onMessageSend={this.handleNewMessage} />
            </div>
        )
    }
}

export default App_chat
