import React from "react";

// reactstrap components
import {
    Badge,
    Card,
    CardHeader,
    CardFooter,
    DropdownMenu,
    DropdownItem,
    UncontrolledDropdown,
    DropdownToggle,
    Media,
    Pagination,
    PaginationItem,
    PaginationLink,
    Progress,
    Table,
    Container,
    Row,
    Button,
    UncontrolledTooltip, Col, CardBody, CardTitle
} from "reactstrap";
// core components
import Header from "components/Headers/Header_RPA.js";
import axios from "axios";
import {Link, Route} from 'react-router-dom'
import Maps from "../driver/Maps";

function icon(e) {
   if (e == false){
       return <i className="fas bold-down"/>
   }
   else if (e == true){
       return <i className="fas fa-address-book-o" />
   }
  }

const DriveTest = (props) => (


    <Card className="card-stats mb-4 mb-xl-0">
        <CardBody>
            <Row>
                <div className="col">
                    <CardTitle
                        tag="h5"
                        className="text-uppercase text-muted mb-0"
                    >
                        {props.drive_test.DriveTest_Title}
                    </CardTitle>
                    <span className="h2 font-weight-bold mb-0">
                        {props.drive_test.DriveTest_Description}
                                                    </span>
                </div>
                <Col className="col-auto">
                    <div className="icon icon-shape bg-danger text-white rounded-circle shadow">

                        {icon(props.drive_test.DriveTest_State)}

                    </div>

                </Col>
            </Row>
            <p className="mt-3 mb-0 text-muted text-sm">
                <span className="text-success mr-2">

            <Link to={"/admin/maps/"+props.drive_test._id}>
               <Button
                   className="btn-neutral btn-icon"
                   color="primary"
                  >
                    Show
               </Button>
            </Link>


                </span>{" "}
                <span className="text-nowrap">
            {
                props.drive_test.DriveTest_Set_Date
            }
                </span>
            </p>
        </CardBody>
    </Card>
)

class RPAList extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            drive_tests : []
        }
    }
    componentDidMount = () => {
        this.interval = window.setInterval(this.moveObject, 1000)
        axios.get('http://localhost:4000/api/planning/get_planning')
            .then(response => {
                this.setState({ drive_tests: response.data });
                this.drive_test = response.data;
                console.log(this.drive_test);
            })
            .catch(function (error){
                console.log(error);
            })
    }
    driveTestList() {


            return this.state.drive_tests.map(function(currentTodo, i){
                for (var cpt = 0; cpt < 8; cpt++) {
                    return <DriveTest drive_test={currentTodo} key={i}/>;
                    console.log(i)
                }
            })
    }
    render() {
        return (
            <>
                <Header />
                {/* Page content */}
                <Container className="mt--7" fluid>
                    {/* Table */}
                    <Row>
                        <div className="col">
                            <Card className="shadow">
                                <CardHeader className="border-0">
                                    <h3 className="mb-0">Card tables</h3>
                                </CardHeader>
                                <br/>
                                <div className="d-flex align-content-around flex-wrap">
                                    {this.driveTestList() }
                                </div>
<br/>
                                <CardFooter className="py-4">
                                    <nav aria-label="...">
                                        <Pagination
                                            className="pagination justify-content-end mb-0"
                                            listClassName="justify-content-end mb-0"
                                        >
                                            <PaginationItem className="disabled">
                                                <PaginationLink
                                                    href="#pablo"
                                                    onClick={e => e.preventDefault()}
                                                    tabIndex="-1"
                                                >
                                                    <i className="fas fa-angle-left" />
                                                    <span className="sr-only">Previous</span>
                                                </PaginationLink>
                                            </PaginationItem>
                                            <PaginationItem className="active">
                                                <PaginationLink
                                                    href="#pablo"
                                                    onClick={e => e.preventDefault()}
                                                >
                                                    1
                                                </PaginationLink>
                                            </PaginationItem>
                                            <PaginationItem>
                                                <PaginationLink
                                                    href="#pablo"
                                                    onClick={e => e.preventDefault()}
                                                >
                                                    2 <span className="sr-only">(current)</span>
                                                </PaginationLink>
                                            </PaginationItem>
                                            <PaginationItem>
                                                <PaginationLink
                                                    href="#pablo"
                                                    onClick={e => e.preventDefault()}
                                                >
                                                    3
                                                </PaginationLink>
                                            </PaginationItem>
                                            <PaginationItem>
                                                <PaginationLink
                                                    href="#pablo"
                                                    onClick={e => e.preventDefault()}
                                                >
                                                    <i className="fas fa-angle-right" />
                                                    <span className="sr-only">Next</span>
                                                </PaginationLink>
                                            </PaginationItem>
                                        </Pagination>
                                    </nav>
                                </CardFooter>
                            </Card>
                        </div>
                    </Row>

                </Container>
            </>
        );
    }
}

export default RPAList;
