    imageCar = 'https://img.icons8.com/offices/30/000000/rv-campground.png';
    get_address_lat_lng = [];
    getDistance = () => {
        // seconds between when the component loaded and now
        const differentInTime = (new Date() - this.initialDate) / 1000 // pass to seconds
        return differentInTime * this.velocity // d = v*t -- thanks Newton!
    }

    componentDidMount = () => {
        this.interval = window.setInterval(this.moveObject, 1000)
        axios.get('http://localhost:4000/api/planning/get_address')
            .then(response => {
                this.setState({ get_address: response.data });
                console.log(response.data);
                this.get_address_lat_lng = response.data;

                console.log(this.get_address_lat_lng);
            })
            .catch(function (error){
                console.log(error);
            })
    }

    componentWillUnmount = () => {
        window.clearInterval(this.interval)
    }

    moveObject = () => {
        const distance = this.getDistance()
        if (!distance) {
            return
        }
        /*function sleep(milliseconds) {
            const date = Date.now();
            let currentDate = null;
            do {
                currentDate = Date.now();
            } while (currentDate - date < milliseconds);
        }*/
        let progress = this.path.filter(coordinates => coordinates.distance < distance)

        const nextLine = this.path.find(coordinates => coordinates.distance > distance)


        var random = 0;

        var count = 0;
        //var markerGreen;


        if (!nextLine) {
            this.setState({progress});
            return // it's the end!
        }
        const lastLine = progress[progress.length - 1];

        const lastLineLatLng = new window.google.maps.LatLng(
            lastLine.lat,
            lastLine.lng
        );

        const nextLineLatLng = new window.google.maps.LatLng(
            nextLine.lat,
            nextLine.lng
        );

        // distance of this line
        const totalDistance = nextLine.distance - lastLine.distance
        const percentage = (distance - lastLine.distance) / totalDistance

        const position = window.google.maps.geometry.spherical.interpolate(
            lastLineLatLng,
            nextLineLatLng,
            percentage
        );

        if (count % 5 === 0) {
            random = Math.random();
            if (random < 0.5) {
                random = Math.floor(random)
            } else {
                random = Math.ceil(random)
            }
            if (random === 0) {
