import React from 'react';
import {withGoogleMap, withScriptjs, GoogleMap, Polyline, Marker} from 'react-google-maps'

class Map extends React.Component {
    state = {
        progress: [],
    }

    path = [
        {lat: 10.198707934404316, lng: 36.776209298858504},
        {lat: 10.200233776057663, lng: 36.777431491380185},
        {lat: 10.20175961771101, lng: 36.777431491380185},
        {lat: 10.20328554318339, lng: 36.776209298858504},
        {lat: 10.206337310309115, lng: 36.776209298858504},
        {lat: 10.20786315196246, lng: 36.776209298858504},
        {lat: 10.20938907743484, lng: 36.776209298858504},
        {lat: 10.212440844560566, lng: 36.77498708684983},
        {lat: 10.213966686213913, lng: 36.77498708684983},
        {lat: 10.21549252786726, lng: 36.77498708684983},
        {lat: 10.217018453339639, lng: 36.77498708684983},
        {lat: 10.218544294992986, lng: 36.77498708684983},
        {lat: 10.220070220465365, lng: 36.77376485535446},
        {lat: 10.22159606211871, lng: 36.77376485535446},
        {lat: 10.22312198759109, lng: 36.77376485535446},
        {lat: 10.226173754716816, lng: 36.772542604372575},
        {lat: 10.227699596370163, lng: 36.77132033390444},
        {lat: 10.22922543802351, lng: 36.77132033390444},
        {lat: 10.230751363495889, lng: 36.77009804395031},
        {lat: 10.232277205149236, lng: 36.77009804395031},
        {lat: 10.233803130621613, lng: 36.768875734510445},
        {lat: 10.236854897747339, lng: 36.768875734510445},
        {lat: 10.23838073940069, lng: 36.768875734510445},
        {lat: 10.239906664873066, lng: 36.76765343915747},
        {lat: 10.241432506526413, lng: 36.76765343915747},
        {lat: 10.244484273652139, lng: 36.76765343915747},
        {lat: 10.247536040777863, lng: 36.76520872285218},
        {lat: 10.249061882431212, lng: 36.76520872285218},
        {lat: 10.252113649556938, lng: 36.76520872285218},
        {lat: 10.253639575029316, lng: 36.76643109074732},
        {lat: 10.255165416682663, lng: 36.76643109074732},
        {lat: 10.259743025461736, lng: 36.76643109074732},
        {lat: 10.261268950934117, lng: 36.76643109074732},
        {lat: 10.262794792587462, lng: 36.76643109074732},
        {lat: 10.264320718059842, lng: 36.76765343915747},
    ]

    velocity = 5
    initialDate = new Date()
    color = "";
    polylineDrawGreen;
    polylineDrawRed;
    arr = [];
    markerRed = [];
    imageFlag = 'https://developers.google.com/maps/documentation/javascript/examples/full/images/beachflag.png';
    image = 'https://img.icons8.com/flat_round/20/000000/error--v1.png';
    imageCar = 'https://img.icons8.com/offices/30/000000/rv-campground.png';

    getDistance = () => {
        // seconds between when the component loaded and now
        const differentInTime = (new Date() - this.initialDate) / 1000 // pass to seconds
        return differentInTime * this.velocity // d = v*t -- thanks Newton!
    }

    componentDidMount = () => {
        this.interval = window.setInterval(this.moveObject, 1000)
    }

    componentWillUnmount = () => {
        window.clearInterval(this.interval)
    }

    moveObject = () => {
        const distance = this.getDistance()
        if (!distance) {
            return
        }
        /*function sleep(milliseconds) {
            const date = Date.now();
            let currentDate = null;
            do {
                currentDate = Date.now();
            } while (currentDate - date < milliseconds);
        }*/
        let progress = this.path.filter(coordinates => coordinates.distance < distance)

        const nextLine = this.path.find(coordinates => coordinates.distance > distance)


        var random = 0;

        var count = 0;
        //var markerGreen;


        if (!nextLine) {
            this.setState({progress});
            return // it's the end!
        }
        const lastLine = progress[progress.length - 1];

        const lastLineLatLng = new window.google.maps.LatLng(
            lastLine.lat,
            lastLine.lng
        );

        const nextLineLatLng = new window.google.maps.LatLng(
            nextLine.lat,
            nextLine.lng
        );

        // distance of this line
        const totalDistance = nextLine.distance - lastLine.distance
        const percentage = (distance - lastLine.distance) / totalDistance

        const position = window.google.maps.geometry.spherical.interpolate(
            lastLineLatLng,
            nextLineLatLng,
            percentage
        );

        if (count % 5 === 0) {
            random = Math.random();
            if (random < 0.5) {
                random = Math.floor(random)
            } else {
                random = Math.ceil(random)
            }
            if (random === 0) {
                console.log("red");
                // this.polylineDrawRed =   <Polyline path={this.state.progress} options={{ strokeColor: "#FF0000"}} />
                //console.log(position.lat()+" , "+ position.lng())
                this.arr.push({lat: position.lat(), lng: position.lng()});
                this.markerRed.push(<Marker position={this.arr[1]}/>);
                progress = progress.concat(position);
                this.setState({progress});

            } else {
                console.log("green")
                this.polylineDrawGreen = <Polyline path={this.state.progress} options={{strokeColor: "#9efd38 "}}/>
                progress = progress.concat(position);
                //  this.setState({progress});
                console.log(!nextLine);

            }
            this.setState({progress});

            console.log(random);
            //sleep(1000)
            // console.log(this.state.progress)
        }


        //this.setState({ progress });
    }

    componentWillMount = () => {
        this.path = this.path.map((coordinates, i, array) => {
                if (i === 0) {
                    return {...coordinates, distance: 0} // it begins here!
                }

                const {lat: lat1, lng: lng1} = coordinates
                const latLong1 = new window.google.maps.LatLng(lat1, lng1)

                const {lat: lat2, lng: lng2} = array[0]
                const latLong2 = new window.google.maps.LatLng(lat2, lng2)

                // in meters:
                const distance = window.google.maps.geometry.spherical.computeDistanceBetween(
                    latLong1,
                    latLong2
                );


                return {...coordinates, distance}
            }
        );
    }


    render = () => {

        return (
            <GoogleMap
                defaultZoom={16}
                defaultCenter={{
                lat: 10.198707934404316, lng: 36.776209298858504,
                }}
            >
                {

                    this.state.progress && (
                        <>
                            <Marker position={this.path[0]} icon={this.imageFlag}/>
                            <Marker position={this.path[this.path.length - 1]} icon={this.imageFlag}/>
                            {this.polylineDrawGreen}
                            <Marker position={this.state.progress[this.state.progress.length - 1]}
                                    icon={this.imageCar}/>

                            {this.arr.map((value, index) => {
                                return <Marker position={this.arr[index]}
                                               icon={this.image}
                                />
                            })}

                        </>

                    )
                }
            </GoogleMap>
        )
    }
}

const MapComponent = withScriptjs(withGoogleMap(Map))

export default () => (
    <MapComponent
        googleMapURL="https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places"
        loadingElement={<div style={{height: `100%`}}/>}
        containerElement={<div style={{height: `650px`, width: '100%'}}/>}
        mapElement={<div style={{height: `100%`}}/>}
    />
)
