=========================================================
* Argon Dashboard React - v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";

// reactstrap components
import {
    Button,
    Card,
    CardHeader,
    CardBody,
    Col, Form, FormGroup, InputGroup, InputGroupAddon, InputGroupText, Input, Row
} from "reactstrap";
import axios from "axios";

class ChatRoom extends React.Component {
    start_rpa_process() {
        /* axios.get('http://localhost:4000/api/rpa')
             .then(res => console.log(res.data));
         alert('Hello!');*/
        var mail = "sellamikhouloud27@gmail.com"
        var txt = "test"
        axios.get('http://localhost:4000/api/sendEmail/'+mail+'/'+txt)
            .then(res => console.log(res.data));
    }
    kill_rpa_process(){
        /* axios.get('http://localhost:4000/api/rpa/killProcess')
             .then(res => console.log(res.data));
         alert('Goodbye !');*/
    }
        render() {
            return (
                <>
                    <Col lg="6" md="8">
                        <Card className="bg-secondary shadow border-0">
                            <CardHeader className="bg-transparent pb-5">
                                <div className="text-muted text-center mt-2 mb-4">
                                    <small>Sign up with</small>
                                </div>

                            </CardHeader>
                            <CardBody className="px-lg-5 py-lg-5">
                                <div className="text-center text-muted mb-4">
                                    <small>Or sign up with credentials</small>
                                </div>
                                <Form role="form">

                                    <FormGroup>
                                        <InputGroup className="input-group-alternative mb-3">
                                            <InputGroupAddon addonType="prepend">
                                                <InputGroupText>
                                                    <i className="ni ni-email-83" />
                                                </InputGroupText>
                                            </InputGroupAddon>
                                            <Input placeholder="Email" type="email" autoComplete="new-email"/>
                                        </InputGroup>
                                    </FormGroup>


                                    <div className="text-center">
                                        <Button className="mt-4" color="primary" type="button"   onClick={this.invite_}>
                                            Invite
                                        </Button>
                                    </div>
                                </Form>
                            </CardBody>
                        </Card>
                    </Col>
                </>
            );
        }
}

export default ChatRoom;
