* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";

// reactstrap components
import {
    Button,
    Card,
    CardHeader,
    CardBody,
    Col, Form, FormGroup, InputGroup, InputGroupAddon, InputGroupText, Input, Row
} from "reactstrap";
import axios from "axios";
import { Link } from 'react-router-dom';
import {string} from "prop-types";
import Search from 'react-search'
import Select from "react-select";


class ChatRoom extends React.Component {
     random_id = '0';
     listEmail = [];
    constructor(props) {
        super(props);
        this.state = {value: ''};
         this.random_id = Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        axios.get('http://localhost:4000/api/sendEmail/')
            .then(res =>
                {
                        for(var i = 0 ; i<res.data.length;i++) {
                            this.listEmail.push({'value': res.data[i]['email'] , 'label': res.data[i]['email']})
                        }
                    console.log(this.listEmail)
                }
            );


    }

   /* handleChange(event) {
        this.setState({value: event.target.value});
    }*/

    handleSubmit(event) {
        alert('Le nom a été soumis : ' + this.state.value);
        event.preventDefault();
    }

    invite(event){
        console.log(this.random_id)
        var mail = this.state.value
        var txt = this.random_id;
        console.log(txt)
        axios.get('http://localhost:4000/api/sendEmail/'+mail+'/'+txt)
            .then(res => console.log(res.data));
       console.log( this.state.value);
    }
    state = {
        selectedOption: null,
    };
    handleChange = selectedOption => {
        this.setState(
            { selectedOption },
            () => console.log(`Option selected:`, this.state.selectedOption)
        );
    };

        render() {
            return (
                <>
                    <Col lg="6" md="8">
                        <Card className="bg-secondary shadow border-0">
                            <CardHeader className="bg-transparent pb-5">
                                <div className="text-muted text-center mt-2">
                                    <big>Call request</big>
                                </div>

                            </CardHeader>
                            <CardBody className="px-lg-5 py-lg-5">
                                <div className="text-center text-muted mb-4">
                                    <big>Search email</big>
                                </div>
                                <Form role="form" onSubmit={this.handleSubmit}>

                                    <FormGroup>
                                        <InputGroup className="input-group-alternative mb-3">
                                            <InputGroupAddon addonType="prepend">
                                                <InputGroupText>
                                                    <i className="ni ni-email-83" />
                                                </InputGroupText>
                                            </InputGroupAddon>
                                            <Input placeholder="Email" type="email" value={this.state.value} onChange={this.handleChange} autoComplete="new-email"/>
                                        </InputGroup>
                                    </FormGroup>

                                    <Select
                                        value={this.state.value}
                                        onChange={this.handleChange}
                                        options={this.listEmail}
                                    />


                                    <div className="text-center">
                                        <Link to={`/App/${this.random_id}`}>


                                        <Button className="mt-4" color="primary" type="button"   onClick={this.invite(this.state.value)}>
                                            Invite
                                        </Button>
                                        </Link>
                                    </div>
                                </Form>
                            </CardBody>
                        </Card>
                    </Col>
                </>
            );
        }
}

export default ChatRoom;
